import React, { useState, useEffect, useRef } from 'react';

export default function Stopwatch() {
  const [time, setTime] = useState(0);

  return (
    <div>
      <h1>{time.toFixed(1)}s</h1>
      <button>Start</button>
      <button>Stop</button>
      <button>Reset</button>
    </div>
  );
}
